INSERT INTO `hotel_commodity` (`id`, `subject`, `content`, `image`, `price`, `category_id`) VALUES
(1, '西堤牛排', '<p>\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;西堤牛排秉承台湾餐饮一贯的精致与情调，很好地满足了现代人们“全身心的享受美味”的要求。在此之余，西堤牛排更提出了“分享我们的幸福滋味”的概念，提出了“饮食美学下的幸福观”,希望消费者在把</span><a target="_blank" href="https://baike.baidu.com/item/%E9%A4%90%E9%A5%AE/2170404">餐饮</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">当享受之余，更能把餐饮当作幸福的体验，并与人们分享幸福滋味</span>\n</p>\n<p>\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;西堤牛排在</span><a target="_blank" href="https://baike.baidu.com/item/%E8%A5%BF%E9%A4%90/1459143">西餐</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">界小有名气，是</span><a target="_blank" href="https://baike.baidu.com/item/%E7%8E%8B%E5%93%81%E9%9B%86%E5%9B%A2">王品集团</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">的中端品牌，作为平民西餐确实有很多值得称道的地方，150元就能享受到七道精致</span><a target="_blank" href="https://baike.baidu.com/item/%E8%A5%BF%E5%BC%8F%E9%A4%90%E7%82%B9/17158293">西式餐点</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">的套餐方式颇受欢迎，味道虽然不能和高级西餐厅比，但每道菜都很精致，口味不会让人失 望，一整套下来吃得饱饱的，用餐的器皿也很别致，吃着自有一番风味。</span>\n</p>', 'u11642535821966598730fm26gp0.jpg', 89, 1),
(2, '台塑牛排', '<p>\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;台塑是一家台湾的集团公司，老板是大亨</span><a target="_blank" href="https://baike.baidu.com/item/%E7%8E%8B%E6%B0%B8%E5%BA%86/19204">王永庆</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">。台塑集团内“御厨”为老板王永庆特别精心研制一种的牛排。王品餐饮集团在他们的餐厅推出了这种牛排，大受欢迎，取名为 台塑牛排。</span>\n</p>\n<p>\n	<a target="_blank" href="https://baike.baidu.com/item/%E7%8E%8B%E5%93%81%E5%8F%B0%E5%A1%91%E7%89%9B%E6%8E%92">&nbsp;&nbsp;&nbsp;&nbsp;王品台塑牛排</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">是中国台湾知名企业台塑集团董事长</span><a target="_blank" href="https://baike.baidu.com/item/%E7%8E%8B%E6%B0%B8%E5%BA%86">王永庆</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">先生招待贵宾的名菜，缘由王董事长经常出席各种宴会， 却不习惯血淋淋的牛排，于是王太太和主厨精心研制出一道全熟牛排，虽然全熟却鲜嫩多汁，这道牛排马上得到王董事长的喜爱,久而久之也成了台塑集团招待贵宾的一道代表菜品！执着于对美味的严格定义，王品历经数千小时的严格选材，精心研发之后，发现一头牛只有第六至第八对肋骨，经72种中西香料腌浸2天2夜，在250℃烤箱烘烤一个半小时，还能保持100%鲜嫩度、独具中国口味的牛排</span>\n</p>', 'u123333044986008005fm26gp0.jpg', 109, 1),
(3, '肉眼牛排', '<p>\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">&nbsp;&nbsp;&nbsp;&nbsp;肉眼牛排是从近肋骨末端的一少部分切下来，该处肉质柔嫩且多汁（有雪花纹），滋味无穷。肉眼牛排一般指取自</span><a target="_blank" href="https://baike.baidu.com/item/%E7%89%9B/2620519">牛</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">身中间的</span><a target="_blank" href="https://baike.baidu.com/item/%E6%97%A0%E9%AA%A8/4122219">无骨</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">部分，[眼] 是指肌肉的圆形横切面，由于这个部分的</span><a target="_blank" href="https://baike.baidu.com/item/%E8%82%8C%E8%82%89/295864">肌肉</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">不会经常活动，所以肉质十分柔软、多汁，并且均匀地布满雪花纹脂肪。市场上多称其为眼肉牛排，是一种错误的称谓。</span>\n</p>\n<p>\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">&nbsp;&nbsp;&nbsp;&nbsp;肉眼牛排可切成薄片煎炒，亦可切成牛排烧烤；肉眼牛排又称作Delmonico牛排（纽约餐厅在18世纪中期使吃牛排普及后）、苏格兰牛</span>\n</p>', 'u1355240386567952467fm26gp0.jpg', 99, 1),
(4, '肋眼牛排', '<p>\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">&nbsp;&nbsp;&nbsp;&nbsp;肋眼牛排是以烤牛靠近胸部的肋肌精心烤制的菜肴。</span>\n</p>\n<p>\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">\n	<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n		1、牛排放在保鲜袋里捶打断筋，这样肉会更细嫩，烤的时候不会回缩；\n	</div>\n	<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n		2、磨些黑胡椒均匀的撒在两面，再涂上黄油、盐，按摩一下，松弛30分钟；\n	</div>\n	<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n		3、烤箱预热220度，牛排放进厚烤盘，中层上下火先烤10分钟；\n	</div>\n	<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n		4、翻面再烤8分钟，就八成熟了。如果喜欢吃嫩一些的，时间可以缩短为（一面8分钟，再一面5分钟）；\n	</div>\n	<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n		5、牛排装盘，利用烤盘里的余温和牛油，再烹进去2勺红酒，最后均匀的浇在烤好的牛排上。鲜嫩多汁的肋眼牛排就烤\n	</div>\n</span>\n</p>', 'u15481438361851434694fm26gp0.jpg', 129, 1),
(5, '菲力牛排', '<p>\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">&nbsp;&nbsp;&nbsp;&nbsp;菲力</span><a target="_blank" href="https://baike.baidu.com/item/%E7%89%9B%E6%8E%92/1156436">牛排</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">四个字中的菲力，指的是</span><a target="_blank" href="https://baike.baidu.com/item/%E7%89%9B%E9%87%8C%E8%84%8A%E8%82%89/807348">牛里脊肉</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">（beef tenderloin）。在澳洲，这块肉被称为“眼菲力”，在法国和英国被称为filet和fillet，中文音译菲力。菲力牛排就是用一定厚度的牛里脊肉做出的牛排。</span>\n</p>\n<p>\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">&nbsp;&nbsp;&nbsp;&nbsp;<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">美味可口，营养丰富</span><br />\n</span>\n</p>', 'u18196887572982606308fm26gp0.jpg', 69, 1),
(6, '沙朗牛排', '<p>\n	<br />\n</p>\n<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n	沙朗牛排<a target="_blank" href="https://baike.baidu.com/item/%E7%89%9B%E6%8E%92/1156436"></a>（Sirloin)，主要是由上腰部的脊肉构成，西冷牛排按质量的不同又可分为小块西冷牛排（entrecte）和大块西冷牛排（sirloin steak）。\n</div>\n<p style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n	事实上<a target="_blank" href="https://baike.baidu.com/item/Sirloin">Sirloin</a>是法语Sur（上）和Loin（柳肉）合成的词，即<a target="_blank" href="https://baike.baidu.com/item/%E7%89%9B%E6%9F%B3/465501">牛柳</a>上方的肉。每份都在250—300克左右。西冷（Sir loin）即下腰肉，也被称为纽约客，因牛上腰部运动量较菲力<a target="_blank" href="https://baike.baidu.com/item/%E6%B2%99%E6%9C%97/5302237">沙朗</a>多，故此部位<a target="_blank" href="https://baike.baidu.com/item/%E8%82%89/3463083">肉</a>质较粗一点。\n</p>\n<p style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">一个传言称，</span><a target="_blank" href="https://baike.baidu.com/item/%E4%BA%A8%E5%88%A9%E5%85%AB%E4%B8%96">亨利八世</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">在品尝过</span><a target="_blank" href="https://baike.baidu.com/item/%E7%89%9B%E6%8E%92">牛排</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">的美味后，把牛排封了爵士，即Sir+loin，中文译为西冷牛排。</span>\n</p>\n<p style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n	<br />\n</p>\n<p>\n	<br />\n</p>\n<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n	<a target="_blank" href="https://baike.baidu.com/item/Sirloin"></a><a target="_blank" href="https://baike.baidu.com/item/%E7%89%9B%E6%9F%B3/465501"></a><a target="_blank" href="https://baike.baidu.com/item/%E6%B2%99%E6%9C%97/5302237"></a><a target="_blank" href="https://baike.baidu.com/item/%E8%82%89/3463083"></a>\n</div>', 'u21765182792749155542fm26gp0.jpg', 106, 1),
(7, '黑椒牛排', '<p>\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">黑椒牛排是一道西餐，主要食材是</span><a target="_blank" href="https://baike.baidu.com/item/%E7%89%9B%E6%8E%92/1156436">牛排</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">和</span><a target="_blank" href="https://baike.baidu.com/item/%E9%BB%91%E8%83%A1%E6%A4%92/9843250">黑胡椒</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">，主要烹饪工艺是煎。牛排营养丰富，有补中益气、滋养脾胃、强健筋骨、化痰息风、止渴止涎的功效。牛肉 味甘，性平，归脾、胃经，有补中益气、滋养脾胃、强健筋骨、化痰息风、止渴止涎的功效，适用于中气下陷、气短体虚，筋骨酸软、贫血久病及面黄目眩之人食用。</span>\n</p>\n<p>\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;"><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">据说，食用牛肉的习惯最早来源于欧洲中世纪时，猪肉及羊肉是平民百姓得食用肉，牛肉则是王公贵族们得高级肉品，尊贵的牛肉被他们搭配上了当时也是享有尊贵身份的胡椒及香辛料一起烹调，并在特殊场合中供应，以彰显主人的尊贵身份。到了18世纪，英国已经成了著名的牛肉食用大国。今天，美国是消费牛肉的最大国家，牛排则早于在十九世纪中叶成了美国人最爱的食用方式。但在亚洲，人们对牛肉有着两级化的反应，比如日本，可说是将牛肉发挥到了极致。各国对牛肉的态度、习惯不同，所以牛肉的食用方法也不同：美国食用牛排的方式粗犷且豪迈，不拘小节，整块腓力牛排烧烤后再切片；罗马风味的佛跳墙则最让人津津乐道，料理后，用油煎至表面成金黄，并注入白葡萄酒，据说这样的料理可以防止夏天的过敏症；对英国人来说，则习惯于将大块的牛排叉起来烤；</span><a target="_blank" href="https://baike.baidu.com/item/%E6%B3%95%E5%BC%8F%E7%89%9B%E6%8E%92">法式牛排</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">特别注重酱汁的调配，用各式的酱汁凸显牛排的尊贵地位；至于德国人，吃牛排的方式非常奇特，“</span><a target="_blank" href="https://baike.baidu.com/item/%E9%85%B8%E7%89%9B%E8%82%89">酸牛肉</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">”光听名字就够让人匪夷所思，而生鲜牛肉则更是需要拿出勇气尝试的；在日本，一般烧烤店中常用的日式照烧烤酱被运用在西式牛排中，使和风照烧沙朗牛排口味别具一格。</span><br />\n</span>\n</p>', 'u23442342993447668147fm26gp0.jpg', 79, 1),
(8, '煎牛排', '<p>\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">煎牛排是一道制作简单的菜品，主要食材是</span><a target="_blank" href="https://baike.baidu.com/item/%E7%89%9B%E6%8E%92/1156436">牛排</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">，主要烹饪工艺是煎。煎牛排属于</span><a target="_blank" href="https://baike.baidu.com/item/%E8%A5%BF%E9%A4%90/1459143">西餐</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">，一般辅以</span><a target="_blank" href="https://baike.baidu.com/item/%E7%BA%A2%E9%85%92/235">红酒</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">食用。在家庭里面也可以烹制简单的牛排，隔15秒就翻面能使表面持续高温,同时内部温度也不会过高,保存肉汁。</span>\n</p>\n<p>\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;"><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">牛肉中所含的人体所需元素，是最多最高最丰富的。其中包括：蛋白质、血质铁、维生素、锌、磷及多种氨基酸。人体在吸收多样化食物的营养中，不可能全部摄取到所需营养，但从牛肉中摄取为最直接和最充整的。而且，牛肉的营养成份很高，人体每天需要的22种氨基酸，一半以上的胺基酸可由人体制造，但有8种氨基酸在人体内无法自制的，称为必需氨基酸，得由食物中摄取。</span><br />\n</span>\n</p>', 'u23634537721689606321fm26gp0.jpg', 89, 1),
(9, '黑胡椒牛排', '<p>\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">黑胡椒牛排是以</span><a target="_blank" href="https://baike.baidu.com/item/%E7%89%9B%E9%87%8C%E8%84%8A%E8%82%89/807348">牛里脊肉</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">为主要食材的</span><a target="_blank" href="https://baike.baidu.com/item/%E5%A4%A7%E4%BC%97%E8%8F%9C/4276529">大众菜</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">，口味咸鲜，</span><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">香嫩美味。</span>\n</p>\n<p>\n	<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n		异域牛排\n	</div>\n	<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n		特点： 异域风味，口味独特。\n	</div>\n<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">用点红酒腌，应该比料酒好</span>\n</p>\n<p>\n	<br />\n</p>', 'u33875956442224748921fm26gp0.jpg', 105, 1),
(10, 'T骨牛排', '<p>\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">T骨牛排（T-bone steak)一般位于牛的上腰部，是一块由脊肉、</span><a target="_blank" href="https://baike.baidu.com/item/%E8%84%8A%E9%AA%A8/878813">脊骨</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">和</span><a target="_blank" href="https://baike.baidu.com/item/%E9%87%8C%E8%84%8A%E8%82%89/1577101">里脊肉</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">等构成的大块牛排。美式T骨牛排(porterhouse steak)形状同T骨牛排，但较T骨牛排大些，一般厚3厘米左右，重450克左右。T骨牛排一般厚2厘米左右，重约300克，小点的200克，厚度1.7厘米。其加工方法是：首先包装袋和多余的脂肪，脂肪要保留一点增加香味，然后按规定的厚度和重量切开外脊肉和里脊肉，最后用锯将脊骨锯断成厚片状即可。</span>\n</p>\n<p>\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">T骨</span><a target="_blank" href="https://baike.baidu.com/item/%E7%89%9B%E6%8E%92">牛排</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">（T-bonesteak）是牛背上的</span><a target="_blank" href="https://baike.baidu.com/item/%E8%84%8A%E9%AA%A8">脊骨</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">肉. 牛排排部位的不同分好多种.做法跟其他牛排一样,也可以要</span><a target="_blank" href="https://baike.baidu.com/item/%E5%8D%8A%E7%86%9F">半熟</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">的. T骨牛排因为骨头点的份量很多,所以一般如果看到菜单上标着450克或500克,不要害怕,因为去掉骨头后,肉的重量就跟其他牛排的普通份是差不多的.</span>\n</p>', 'u35215751021833875057fm26gp0.jpg', 99, 1),
(11, '嫩牛柳', '<p>\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">香嫩牛肉是由牛肉等主要材料做成的一道菜品，属于家常菜。</span>\n</p>\n<p>\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">\n	<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n		1.牛肉富含蛋白质，氨基酸组成比猪肉更接近人体需要，能提高机体抗病能力，对生长发育及术后，病后调养的人在补充失血、修复组织等方面特别适宜，寒冬食牛肉可暖胃，是该季节的补益佳品；\n	</div>\n	<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n		2.牛肉有补中益气，滋养脾胃，强健筋骨，化痰息风，止渴止涎之功效，适宜于中气下隐、气短体虚、筋骨酸软、贫血久病及面黄目眩之人食用；\n	</div>\n	<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n		3.水牛肉能安胎补神，黄牛肉能安中益气、健脾养胃、强筋壮骨。\n	</div>\n</span>\n</p>', 'u16340249573124867081fm26gp0.jpg', 59, 1),
(12, '细面', '<p>\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">炒细面是一道由</span><a target="_blank" href="https://baike.baidu.com/item/%E9%BE%99%E9%A1%BB%E9%9D%A2/1247948">龙须面</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">、</span><a target="_blank" href="https://baike.baidu.com/item/%E5%8D%B7%E5%BF%83%E8%8F%9C/2073007">卷心菜</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">等食材制成的美食。</span>\n</p>\n<p>\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">早上要做饭。想起来使我们臭蛋的生日，我们这的习俗。生日要吃长寿面，所以就做了炒细面，祝我们家臭蛋身体健康</span>\n</p>', '1036844481965677840fmgp.jpg', 29, 2),
(13, '螺旋面', '<p>\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">意大利螺旋面制作主料：</span><a target="_blank" href="https://baike.baidu.com/item/%E8%9E%BA%E6%97%8B%E9%9D%A2/4155289">螺旋面</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">适量、</span><a target="_blank" href="https://baike.baidu.com/item/%E7%95%AA%E8%8C%84/69104">番茄</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">&nbsp;一个（小）、</span><a target="_blank" href="https://baike.baidu.com/item/%E7%8C%AA%E8%82%89/486463">猪肉</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">碎50克、</span><a target="_blank" href="https://baike.baidu.com/item/%E6%B4%8B%E8%91%B1/6409">洋葱</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">适量、蒜末 一枚、盐适量、料酒适量、糖适量、黑胡椒适量、番茄酱适量。</span>\n</p>\n<p>\n	<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n		1.番茄、洋葱切丁备用\n	</div>\n	<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n		2.猪肉+料酒腌10分钟\n	</div>\n	<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n		3.炒锅加油爆香蒜末，入猪肉炒至变白后起锅\n	</div>\n	<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n		4.另起锅，入洋葱炒至略透明、下番茄炒至软\n	</div>\n	<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n		5.加入适量番茄酱及水，烧开后转小火，加入适量盐和糖，小火慢煮\n	</div>\n	<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n		6.待煮至软即可起锅，起锅前将炒过的猪肉加入略煮\n	</div>\n	<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n		7.水中加少许盐和色拉油，烧开后下螺旋面煮至熟，捞起，淋上煮好的酱汁，撒上黑胡椒即可\n	</div>\n	<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n		意大利螺旋面<span class="sup--normal" style="font-size:12px;line-height:0;vertical-align:baseline;color:#3366CC;">&nbsp;[1]</span><a name="ref_[1]_9277793"></a>&nbsp;\n		<div class="lemma-picture text-pic layout-right" style="border:1px solid #E0E0E0;margin:0px 0px 3px 20px;">\n			<a class="image-link" href="https://baike.baidu.com/pic/%E6%84%8F%E5%A4%A7%E5%88%A9%E8%9E%BA%E6%97%8B%E9%9D%A2/2110008/0/4a36acaf2edda3cc8158f6be01e93901203f92f4?fr=lemma&amp;ct=single" target="_blank"></a>\n		</div>\n	</div>\n</p>', 'u8135821613678503144fm15gp0.jpg', 39, 2),
(14, '长面', '<p>\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">长面又叫“</span><a target="_blank" href="https://baike.baidu.com/item/%E9%95%BF%E5%AF%BF%E9%9D%A2/188442">长寿面</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">” “长根面”，是宋元明清时期</span><a target="_blank" href="https://baike.baidu.com/item/%E9%BB%84%E9%BE%99%E6%BA%AA%E9%95%87/10976276">黄龙溪镇</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">的著名特色传统小吃，一般只有过年过寿办喜事才吃。给老人祝寿时吃上一根长面，祝福老人健康长寿。婚嫁喜事时新郎新娘必须吃上一根长面，而且是新郎新娘分别从一根长面的两端向中间吃，直吃到嘴对嘴合二为一。寓意长长久久、</span><a target="_blank" href="https://baike.baidu.com/item/%E7%99%BE%E5%B9%B4%E5%A5%BD%E5%90%88/2410">百年好合</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">。正月里来吃上一根长面，预示全年一顺百顺。</span>\n</p>\n<p>\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">由于黄龙溪长面制作工艺相当复杂，随着机械制面逐步取代手工，以前家家户户都会做的长面现在几乎失传！现在也只有位于仿清街尾的少数几家人能完整的制作”黄龙溪长面“。据说长面制作从和面到面下锅总共需要十几道工序，历时十多个小时。</span>\n</p>\n<p>\n	<br />\n</p>', 'u1127606688397800708fm26gp0.jpg', 33, 2),
(15, '粒粒面', '<p>\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">颗粒面，又称粒粒面、珍珠面，属于花色挂面，是婴幼儿面类辅食的一种。</span>\n</p>\n<p>\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;"><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">众所周知，面条具有利于消化、吸收的优势，能够呵护肠胃健康。对于肠胃系统还比较脆弱的婴幼儿来说，面条是添加辅食后比较好的选择。而颗粒面在发扬面条有利于消化、吸收优势的基础上，采用特殊工艺，将面粉合成均匀小疙瘩状，在煮制过程中，面体糊状，弱化了小颗粒对于还不会咀嚼的宝宝带来的吸收困扰，更好消化、吸收。</span></span>\n</p>\n<p>\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;"><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">越来越成熟的厂家会在加工过程中根据中国婴童的身体发展需要而添加相应的营养素，大家都知道，营养过剩同营养缺乏同样可怕。像钙、铁、锌、硒、烟酸、维生素B1、维生素B2等营养素，是婴童身体所必须的，因此妈妈们可以通过营养成分表来挑选添加了这7种营养素的颗粒面。</span><br />\n</span>\n</p>', 'u21557232402242918875fm26gp0.jpg', 19, 2),
(16, '手擀面', '<p>\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">手擀面，是</span><a target="_blank" href="https://baike.baidu.com/item/%E9%9D%A2%E6%9D%A1/386300">面条</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">的一种，因用手工擀出，所以称之为“手擀面”。面条的制作方法多种多样，有擀、抻、切、削、揪、压、搓、拨、捻、剔、拉等。</span>\n</p>\n<p>\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">最早的实物面条是由中国科学院地质与地球</span><a target="_blank" href="https://baike.baidu.com/item/%E7%89%A9%E7%90%86%E7%A0%94%E7%A9%B6%E6%89%80/2091978">物理研究所</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">的科学家发现的，他们在2005年10月14日在黄河上游、青海省</span><a target="_blank" href="https://baike.baidu.com/item/%E6%B0%91%E5%92%8C%E5%8E%BF">民和县</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">喇家村进行地质考察时，在一处河漫滩沉积物地下3米处，发现了一个倒扣的碗。碗中装有</span><a target="_blank" href="https://baike.baidu.com/item/%E9%BB%84%E8%89%B2/32230">黄色</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">的面条，最长的有50厘米。研究人员通过分析该物质的成分，发现这碗面条已经有约4000年历史，使面条的历史大大提前。面条最初只称为“</span><a target="_blank" href="https://baike.baidu.com/item/%E9%A5%BC">饼</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">”，“水溲饼、</span><a target="_blank" href="https://baike.baidu.com/item/%E7%85%AE%E9%A5%BC">煮饼</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">”便是</span><a target="_blank" href="https://baike.baidu.com/item/%E4%B8%AD%E5%9B%BD%E9%9D%A2%E6%9D%A1">中国面条</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">先河—— “饼，并也，溲面使合并也”（引：</span><a target="_blank" href="https://baike.baidu.com/item/%E5%88%98%E7%86%99">刘熙</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">《</span><a target="_blank" href="https://baike.baidu.com/item/%E9%87%8A%E5%90%8D">释名</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">》），其意指用水将面粉和在一起所做出的食品均称之为“饼”；以水煮的面条或面块亦全作“饼”称。 ，是大众的重要主食之一，并深受人们的喜爱。近年来，面条的品种越来越丰富，但家常手擀面却被忽视了。</span>\n</p>\n<p>\n	<br />\n</p>', 'u2358335182421890886fm26gp0.jpg', 29, 2),
(17, '尖面', '<p>\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">剔尖又称拨鱼、剔拨股，原产地</span><a target="_blank" href="https://baike.baidu.com/item/%E5%B1%B1%E8%A5%BF%E7%9C%81/365266">山西省</a><a target="_blank" href="https://baike.baidu.com/item/%E6%99%8B%E4%B8%AD%E5%B8%82/529804">晋中市</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">昔阳县，是流行于晋中一带的地方传统经典</span><a target="_blank" href="https://baike.baidu.com/item/%E9%9D%A2%E9%A3%9F/2908690">面食</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">，简单说就是“用筷子拨出的</span><a target="_blank" href="https://baike.baidu.com/item/%E9%9D%A2%E6%9D%A1/386300">面条</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">”。高手剔出的剔尖，呈中间圆、两头尖形状，用</span><a target="_blank" href="https://baike.baidu.com/item/%E7%AD%B7%E5%AD%90/249194">筷子</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">沿盘边剔面，筋软爽口，易于</span><a target="_blank" href="https://baike.baidu.com/item/%E6%B6%88%E5%8C%96/403663">消化</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">。配上荤素打卤，吃起来又香又滑十分可口。</span>\n</p>\n<p>\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">剔尖的面要掌握和面的稀度，否则很难剔出又长又不会断的</span><a target="_blank" href="https://baike.baidu.com/item/%E9%9D%A2%E6%9D%A1">面条</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">。在家制作剔尖面一般用小麦面就可以，先将面和到比</span><a target="_blank" href="https://baike.baidu.com/item/%E9%A5%BA%E5%AD%90">饺子</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">面稍微稀一点，再一点一点加水，一边用筷子向一个方向不停搅拌至光滑上劲，拉起面可以自由流下又不会断的程度。和好的面要饧1－2小时最好。</span>\n</p>', 'u24422725853755136618fm26gp0.jpg', 41, 2),
(18, '油泼面', '<p>\n	<br />\n</p>\n<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n	油泼面是陕西传统的特色面食之一，起源于明代，有鲜香味、酸辣味、香辣味。\n</div>\n<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n	油泼面是一种很普通的面食制作方法，将手工制作的面条在开水中煮熟后捞在碗里，将葱花碎、<a target="_blank" href="https://baike.baidu.com/item/%E8%8A%B1%E6%A4%92%E7%B2%89/10893070">花椒粉</a>、<a target="_blank" href="https://baike.baidu.com/item/%E7%9B%90/548739">盐</a>等配料和厚厚一层的辣椒面一起平铺在面上，用烧的滚烫的菜油浇在调料上，顿时热油沸腾，将花椒面、辣椒面烫熟而满碗红光，随后调入适量<a target="_blank" href="https://baike.baidu.com/item/%E9%85%B1%E6%B2%B9/1246">酱油</a>、香醋即可。也可另外加入<a target="_blank" href="https://baike.baidu.com/item/%E8%85%8A%E6%B1%81%E8%82%89/6199892">腊汁肉</a>、青椒土豆丝、青椒鸡蛋、<a target="_blank" href="https://baike.baidu.com/item/%E8%A5%BF%E7%BA%A2%E6%9F%BF">西红柿</a><a target="_blank" href="https://baike.baidu.com/item/%E9%B8%A1%E8%9B%8B/6405">鸡蛋</a>等搭配食用。\n</div>\n<p>\n	<br />\n</p>', 'u24948176753832729009fm26gp0.jpg', 25, 2),
(19, '粗通心面', '<p>\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">通心粉亦称通心面，在国外已是极普通的面制品之一，我国</span><a target="_blank" href="https://baike.baidu.com/item/%E4%B8%8A%E6%B5%B7/114606">上海</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">、广州、</span><a target="_blank" href="https://baike.baidu.com/item/%E5%A4%A9%E6%B4%A5/132308">天津</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">等地也有少量生产。通心粉的种类很多，一般都是选用淀粉质丰富的粮食经粉碎、胶化、加味、挤压、烘干而制成各种各样口感良好、风味独特的面类食品。</span>\n</p>\n<p>\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">通心粉是西方国家的著名</span><a target="_blank" href="https://baike.baidu.com/item/%E9%9D%A2%E9%A3%9F">面食</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">，有实心和空心之分。亦称</span><a target="_blank" href="https://baike.baidu.com/item/%E9%80%9A%E5%BF%83%E9%9D%A2">通心面</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">，国际上统称麦卡罗尼，是一种以小麦粉为原料的面制品。通心粉汁可以留在空心的通心粉里，吃起来比较不容易干。</span>\n</p>\n<p>\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">18世纪，</span><a target="_blank" href="https://baike.baidu.com/item/%E6%84%8F%E5%A4%A7%E5%88%A9">意大利</a><a target="_blank" href="https://baike.baidu.com/item/%E9%82%A3%E4%B8%8D%E5%8B%92%E6%96%AF">那不勒斯</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">附近的一家经营</span><a target="_blank" href="https://baike.baidu.com/item/%E9%9D%A2%E6%9D%A1">面条</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">和面片的店主</span><a target="_blank" href="https://baike.baidu.com/item/%E9%A9%AC%E5%8D%A1%E7%BD%97%E5%B0%BC">马卡罗尼</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">，他的小女儿在玩耍时把面片卷成空心条并晾于衣绳上。</span><a target="_blank" href="https://baike.baidu.com/item/%E9%A9%AC%E5%8D%A1%E7%BD%97%E5%B0%BC">马卡罗尼</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">将空心条煮熟后拌以</span><a target="_blank" href="https://baike.baidu.com/item/%E7%95%AA%E8%8C%84%E9%85%B1">番茄酱</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">。此后他建造了世界第一家通心粉加工厂，并以自己的名字为通心粉命名。</span>\n</p>', 'u3258168899815694470fm26gp0.jpg', 36, 2),
(20, '千层面', '<p>\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">意大利千层面是美食王国</span><a target="_blank" href="https://baike.baidu.com/item/%E6%84%8F%E5%A4%A7%E5%88%A9/148336">意大利</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">的一种菜品，属于意式宽面，采用的佐料主要是西红柿或者</span><a target="_blank" href="https://baike.baidu.com/item/%E7%95%AA%E8%8C%84%E9%85%B1/3272179">番茄酱</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">。千层面，带给人的感觉就是生活之上的一种享受，尽管每层都是铺进了一样的馅料，可是吃每层的感觉却都不一样。</span>\n</p>\n<p>\n	<a target="_blank" href="https://baike.baidu.com/item/%E5%8D%83%E5%B1%82%E9%A5%BC">千层饼</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">是意大利非常有名并推荐给世界各地的一道菜，它是由4或5层新鲜的</span><a target="_blank" href="https://baike.baidu.com/item/%E9%B8%A1%E8%9B%8B%E9%9D%A2">鸡蛋面</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">组成，每层填入盐、奶油调味汁、</span><a target="_blank" href="https://baike.baidu.com/item/%E5%8D%9A%E6%B4%9B%E5%B0%BC%E4%BA%9A">博洛尼亚</a><a target="_blank" href="https://baike.baidu.com/item/%E8%A5%BF%E7%BA%A2%E6%9F%BF">西红柿</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">肉末卤和</span><a target="_blank" href="https://baike.baidu.com/item/%E5%B8%95%E5%B0%94%E7%8E%9B">帕尔玛</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">干</span><a target="_blank" href="https://baike.baidu.com/item/%E5%A5%B6%E9%85%AA">奶酪</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">，通常做成传统的长条形。</span>\n</p>', 'u33562086662674084501fm26gp0.jpg', 59, 2),
(21, '通心粉', '<p>\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">香嫩牛肉是由牛肉等主要材料做成的一道菜品，属于家常菜。</span>\n</p>\n<p>\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">\n	<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n		1.牛肉富含蛋白质，氨基酸组成比猪肉更接近人体需要，能提高机体抗病能力，对生长发育及术后，病后调养的人在补充失血、修复组织等方面特别适宜，寒冬食牛肉可暖胃，是该季节的补益佳品；\n	</div>\n	<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n		2.牛肉有补中益气，滋养脾胃，强健筋骨，化痰息风，止渴止涎之功效，适宜于中气下隐、气短体虚、筋骨酸软、贫血久病及面黄目眩之人食用；\n	</div>\n	<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n		3.水牛肉能安胎补神，黄牛肉能安中益气、健脾养胃、强筋壮骨。\n	</div>\n</span>\n</p>', 'u3666395599858517426fm26gp0.jpg', 33, 2),
(22, '乌冬面', '<p>\n	<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n		乌冬面（<a target="_blank" href="https://baike.baidu.com/item/%E6%97%A5%E6%96%87/3463137">日文</a>：うどん，英文：udon，在<a target="_blank" href="https://baike.baidu.com/item/%E6%97%A5%E6%96%87%E6%B1%89%E5%AD%97/10240831">日文汉字</a>中写为：饂饨），又作乌龙，是一种日本面食。是以<a target="_blank" href="https://baike.baidu.com/item/%E5%B0%8F%E9%BA%A6/10237">小麦</a>为原料制造的，在<a target="_blank" href="https://baike.baidu.com/item/%E7%B2%97%E7%BB%86/9607026">粗细</a>和<a target="_blank" href="https://baike.baidu.com/item/%E9%95%BF%E5%BA%A6/1584632">长度</a>方面有特别的规定。\n	</div>\n	<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n		乌冬面在<a target="_blank" href="https://baike.baidu.com/item/%E6%97%A5%E6%9C%AC/111617">日本</a>有很多种类，有<a target="_blank" href="https://baike.baidu.com/item/%E7%A7%8B%E7%94%B0/22535">秋田</a>的稻庭乌冬面、群马的水泽乌冬面、埼玉的加须乌冬面等。\n	</div>\n<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">乌冬面是最具日本</span><a target="_blank" href="https://baike.baidu.com/item/%E7%89%B9%E8%89%B2/3690806">特色</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">的面条之一，与日本的</span><a target="_blank" href="https://baike.baidu.com/item/%E8%8D%9E%E9%BA%A6%E9%9D%A2">荞麦面</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">、绿茶面并称日本三大面条，是日本料理店</span><span class="sup--normal" style="line-height:0;vertical-align:baseline;color:#3366CC;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">&nbsp;[1]</span><a name="ref_[1]_31228"></a>&nbsp;<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">&nbsp;不可或缺的主角。其口感介于切面和</span><a target="_blank" href="https://baike.baidu.com/item/%E7%B1%B3%E7%B2%89">米粉</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">之间，口感偏软，再配上精心调制的汤料，就成了一道可口的面食。是将盐和水混入面粉中制作成的白色较粗（直径4毫米～6毫米）的面条。冬天加入</span><a target="_blank" href="https://baike.baidu.com/item/%E7%83%AD%E6%B1%A4">热汤</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">、夏天则放凉食用。凉乌冬面可以蘸被叫做“面佐料汁”的浓料汁食用。</span>\n</p>', 'u40870596113326685254fm26gp0.jpg', 26, 2),
(23, '日式照烧滋烤鳗鱼比萨', '<span style="font-family:宋体;font-size:16px;background-color:#FFFFFF;">日式照烧滋烤鳗鱼比萨是一道兼具丰富内涵与高颜值的诱人美味!肉质丰腴的鳗鱼,历经先烤、再蒸、再烤等多重繁复精细的工艺制作,表面金黄酥香,入口软糯绵密。在色泽光亮、口感浓郁的照烧酱汁加持下,鳗鱼被注入了灵魂,不仅锁住其细嫩本色,更增强了鲜香滋味。辅以宛如星星的秋葵和金黄的玉米粒,在海苔和芝麻的点缀下,一道鲜美可口、自带颜值滤镜的美味便跃然眼前!入口处,滑嫩鳗鱼的鲜香与手拍饼底的酥香充盈唇齿之间,让人食欲大开,收获“鳗”载而归的幸福感,不禁盛赞美食不可辜负!</span>\n<p>\n	<br />\n</p>\n<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n</div>\n<p>\n	<br />\n</p>\n<p>\n	<span style="font-family:宋体;font-size:16px;background-color:#FFFFFF;"><span style="font-family:宋体;font-size:16px;background-color:#FFFFFF;">冬日已至,寒意渐浓。“比萨外送专家”达美乐比萨充分发挥自身优势,只需30分钟,让你足不出户便可尽享美味快热送!达美乐比萨中国承诺“外送30分钟必达,超时送免费比萨券”(详情请见免费比萨心意券使用规则或官网说明),更有GPS订单实时追踪,让您订餐更安心!</span><br />\n</span>\n</p>', '2852888451339205094fm26gp0.jpg', 39, 3),
(24, '巴伐利亚风味猪肘盛享比萨', '<p>\n	<span style="font-family:宋体;font-size:16px;background-color:#FFFFFF;"><span style="color:#2B2B2B;font-family:PingFangSC-Regular, &quot;font-size:16px;background-color:#FFFFFF;">巴伐利亚风味猪肘盛享比萨可以说是肉食主义者的狂欢盛宴,丰盛又美味。精选猪前腿肉制作而成的大片猪肘,不仅肉感充实,弹性满满,经过风味香料腌制,肉质更是柔嫩多汁;铺在被精心手拍至厚薄适中的比萨饼底上,呈现出狂野又奔放的风格,简直是肉食主义者的福音,让你大快朵颐,得到极大的满足。而点缀在猪肘之上的酸椰菜,专门从德国进口,可以说是比萨的点睛之笔,不但为这一美食增添纯正且地道的德国风味,也丰富了层次,吃起来香而不腻,让人拥有一种满满的幸福感。</span></span>\n</p>\n<p>\n	<span style="font-family:宋体;font-size:16px;background-color:#FFFFFF;"><span style="color:#2B2B2B;font-family:PingFangSC-Regular, &quot;font-size:16px;background-color:#FFFFFF;">新品于11月2日抢鲜上市,在达美乐比萨门店、官方APP、微信公众号、小程序、官网等均有出售。原价购买更免外送费!周二周三通过官方网上渠道订餐,比萨更享7折优惠!此外,通过达美乐官方网上订餐渠道任意消费后,可参与抽奖,有机会赢取免费比萨、半价比萨、小食等,人人有礼。想亲尝德式美味的小伙伴们速来达美乐尝新吧!</span><br />\n</span> \n</p>', 'u19704751041499883867fm26gp0.jpg', 29, 3),
(25, '扇贝大虾豪华比萨', '<p>\n	<span style="font-family:宋体;font-size:16px;background-color:#FFFFFF;">据了解，此次推出的扇贝大虾豪华比萨由达美乐比萨大师倾力打造。品质肥美的大扇贝与饱满的开背大虾和Q弹鲜甜的虾仁“牵手”所组成的豪华配料阵容，错落有致地铺在被精心手拍至厚薄适中的比萨饼底，融于奶香浓郁的进口芝士中。清甜的扇贝大虾和虾仁，与脆嫩爽滑的秋葵、酸甜适宜的樱桃番茄、韧性松脆的饼底同时入口，唇齿间迸发处的丰富口感带来预测不到的鲜美体验，令人垂涎。</span>\n</p>\n<p>\n	<span style="font-family:宋体;font-size:16px;background-color:#FFFFFF;"><span style="font-family:宋体;font-size:16px;background-color:#FFFFFF;">而此次新推出的洋葱黄金圈，则是一款西式经典产品。酥香美味的洋葱黄金圈，入口浓郁鲜香。搭配美国风情土豆培根比萨、照烧风味牛肉土豆比萨等经典风味比萨，或是鲜嫩多汁的烤翅，乐享西式经典美味！</span><br />\n</span>\n</p>', 'u22723446871848509716fm26gp0.jpg', 69, 3),
(26, '果肉榴莲比萨', '<p>\n	<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n		榴莲披萨是由水果之王-榴莲与奶酪、披萨饼所烤成的水果味披萨。\n	</div>\n	<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n		榴莲披萨是由浓郁的进口芝士搭配上姜黄色的榴莲果肉，味浓，奶油味四溢，果肉拥有凝脂似的质感，层次丰富，入口嫩滑细致，外形、色泽、肉质近乎完美。\n	</div>\n<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">在专用的260℃烤箱中，烤制5分0秒，标准的原料用量，精确的烤制时间，使得每一个披萨的出品都得到保证，芝士香浓，口感拉丝恰到好处，榴莲果肉饱满，入口嫩滑，甜而不腻。</span>\n</p>\n<p>\n	<br />\n</p>', 'u2337281773221235715fm26gp0.jpg', 59, 3),
(27, '雪蟹虾仁富贵比萨', '<p>\n	<br />\n</p>\n<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n	<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n		雪蟹：学名：灰眼雪蟹（科学命名）：Chionoecetes opilio, Chionoecetes bairdi。俗名：雪蟹，皮匠蟹（美国），皇后蟹（加拿大和欧洲）（Snow Crab，Queen Crab） 个体大小：灰眼雪蟹700—1100克；红眼雪蟹1400 —2500克。\n	</div>\n	<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n		雪蟹主要产地：美国阿拉斯加。产地在北大西洋（加拿大大西洋沿岸各省）和北太平洋（俄罗斯远东、美国阿拉斯加、日本北海道）等水域均有捕捞。质量以加拿大新不伦维克省沿岸的圣劳伦斯湾捕捞的雪蟹外观、口感最好。规格在700-1100克。出成率为活蟹到煮熟蟹段60%，活蟹到熟蟹肉17%，\n	</div>\n<a target="_blank" href="https://baike.baidu.com/item/%E9%9B%AA%E8%9F%B9"><span style="font-weight:700;">雪蟹</span></a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">的主要产地在澳大利亚西南部，蟹壳颜色比较淡，比较软，而肉厚膏肥，可谓是皮薄馅大。活</span><span style="font-weight:700;color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">雪蟹</span><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">新鲜煮制的蟹肉特别甜美，也便于制作，蒸煮焖都很适合。</span><br />\n</div>', 'u2496010718994690864fm26gp0.jpg', 69, 3),
(28, '小龙虾酥香嫩鸡比萨', '<p>\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">香酥鸡是</span><a target="_blank" href="https://baike.baidu.com/item/%E5%B1%B1%E4%B8%9C%E7%9C%81/209822">山东省</a><a target="_blank" href="https://baike.baidu.com/item/%E9%9D%92%E5%B2%9B%E5%B8%82/785198">青岛市</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">的一道特色名菜，属于鲁菜系，也是青岛十大特色名菜之一</span><span class="sup--normal" style="line-height:0;vertical-align:baseline;color:#3366CC;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">&nbsp;[1]</span><a name="ref_[1]_135595"></a>&nbsp;<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">&nbsp;，其传遍</span><a target="_blank" href="https://baike.baidu.com/item/%E6%B5%8E%E5%8D%97/135066">济南</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">、青岛、</span><a target="_blank" href="https://baike.baidu.com/item/%E7%83%9F%E5%8F%B0/207299">烟台</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">等地区。其选用笋母鸡，以</span><a target="_blank" href="https://baike.baidu.com/item/%E9%AB%98%E6%B1%A4/575974">高汤</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">蒸熟，火候足到，入油再炸，焦酥异常，其色红润，肉烂味美，是佐酒之</span><a target="_blank" href="https://baike.baidu.com/item/%E7%BE%8E%E5%91%B3/5430284">美味</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">。</span>\n</p>\n<p>\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">香酥鸡是</span><a target="_blank" href="https://baike.baidu.com/item/%E9%9D%92%E5%B2%9B/60244">青岛</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">特色名菜之一，选用笋母鸡，以高汤蒸熟，火候足到，入油再炸，焦酥异常，其色红润，肉烂味美，是佐酒之美味。</span>\n</p>', 'u259171361015162000fm26gp0.jpg', 89, 3),
(29, '培根多多肉香粉松土豆比萨', '<p>\n	<span style="color:#333333;font-family:arial;font-size:16px;background-color:#FFFFFF;">培根多多肉香粉松土豆比萨，鲜香弹韧、肉质细腻的培根加量!加量!一口咬下去肉感十足，唇齿留香。搭配网红肉香粉松，松软醇厚，喷香满溢。精选美国进口黄金薯角，外酥里糯。佐以饱满弹嫩的意式香肠丁和青豆，融入馥郁浓香的进口芝士，韧香的饼底，咸香甜软的馅料，经典滋味全新升级!让你一吃倾心，一口难忘!</span>\n</p>\n<p>\n	<span style="color:#333333;font-family:arial;font-size:16px;background-color:#FFFFFF;">新品比萨于6月15日在达美乐上市，价格更是一如既往的亲民。9”仅为69元，更免外送费!周二周三网上订餐（官网/微信/APP），比萨更享7折优惠!想亲尝美味的小伙伴们速来达美乐尝新吧!</span>\n</p>', 'u26255203022357529128fm26gp0.jpg', 69, 3),
(30, '帕斯雀牛肉珍享比萨', '<p>\n	<span style="color:#333333;font-family:sinmsun;font-size:16px;background-color:#FFFFFF;">帕斯雀牛肉向来都是高雅与品位的代名词，选料精细、烹饪考究，更是意式大餐人气食材之一。其肉质柔嫩，经丰富香料熏制，黑椒风味溢出，咸香润泽，作为西餐桌上当之无愧的宠儿，享誉全球。</span>\n</p>\n<p>\n	<span style="color:#333333;font-family:sinmsun;font-size:16px;background-color:#FFFFFF;">帕斯雀牛肉珍享比萨，甄选大片帕斯雀牛肉，其肉质鲜美，嫩滑柔软，融入进口芝士，烘烤后香气四溢，一口咬下，丰富香料的香气充盈口腔，妙不可言。再配上与生俱来西餐范儿的芦笋，爽脆鲜嫩，佐以多汁的番茄，享用珍馐的感觉油然而生。这个盛夏的尊享美味——帕斯雀牛肉珍享比萨，9”仅69元起，更免外送费！周二周三更有网上订餐比萨7折，仅49元起！美味与视觉的双重享受，不容错过！</span>\n</p>', 'u2713430925600779065fm26gp0.jpg', 49, 3),
(31, '照烧风味章鱼虾仁比萨', '<p>\n	<span style="font-family:宋体, Arial, sans-serif;font-size:16px;background-color:#FFFFFF;">火热夏日，边吃比萨边来上一杯冰凉清爽又营养丰富的果汁，那是再快活不过了！达美乐还推出了两款夏日缤纷特饮，“柚”在卖“檬”： 富含多种维生素的白西柚与维C丰富的柠檬相遇，让每一口都交织出阳光般的清新，沁心爽口！另一款红“橙”之恋——精选维C满格的血橙与鲜橙混合，“橙双橙对“，一口沁透心田，令人满心欢悦！</span>\n</p>\n<p>\n	<span style="font-family:宋体, Arial, sans-serif;font-size:16px;background-color:#FFFFFF;">据悉，照烧章鱼鲜虾比萨与夏日特饮将于7月21日起正式在达美乐全国各门店及官网/微信/APP同步上市！照烧章鱼鲜虾比萨， 9”仅69元，周二周三网上订餐享7折，仅48元！美味不容错过！至2017年8月31日，在官网/微信/APP/门店订餐，买原价照烧章鱼鲜虾比萨，即享免外送费！夏日特饮，第二杯半价！</span>\n</p>', 'u3470594554236538398fm26gp0.jpg', 59, 3),
(32, '肉香四溢比萨', '<p>\n	<span style="color:#A5A5A5;font-family:Avenir, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FFFFFF;">多种肉食与进口芝士融合，肉香肆溢！</span><br />\n<span style="color:#A5A5A5;font-family:Avenir, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FFFFFF;">配料：火腿、意式香肠片、意式香肠丁、洋葱、培根、樱桃番茄、芝士</span>\n</p>\n<p>\n</p>', 'u35659186403799516815fm26gp0.jpg', 79, 3),
(33, '金枪鱼比萨', '<p>\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">金枪鱼披萨是一款披萨，制作原料主要有金枪鱼、高粉、干酵母等。</span>\n</p>\n<p>\n	<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n		1、马苏里拉刨丝备用，菠萝切成薄厚均匀的丁状。\n	</div>\n	<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n		2、飞饼皮一张，在披萨盘或烤盘上抹少许食用油，将飞饼皮放入。\n	</div>\n	<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n		3、饼皮上涂一层薄薄的番茄沙司，撒适量帕玛森奶酪。\n	</div>\n	<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n		4、一层金枪鱼肉。\n	</div>\n	<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n		5、切好的菠萝和青豆放到饼皮上。\n	</div>\n	<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n		6、撒适量马苏里拉奶酪。\n	</div>\n	<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n		7、箱预热，175度烤10分钟即可。<span class="sup--normal" style="font-size:12px;line-height:0;vertical-align:baseline;color:#3366CC;">&nbsp;[1]</span>\n	</div>\n</p>', 'u3119075113135877148fm26gp0.jpg', 88, 3),
(34, '芝麻糊', '<p>\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">芝麻糊（hù）是以</span><a target="_blank" href="https://baike.baidu.com/item/%E9%BB%91%E8%8A%9D%E9%BA%BB/405556">黑芝麻</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">为主要原料，加入其它</span><a target="_blank" href="https://baike.baidu.com/item/%E4%BA%94%E8%B0%B7%E6%9D%82%E7%B2%AE/94692">五谷杂粮</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">制作而成的一种食品。自古以来就被认为是滋补强身、</span><a target="_blank" href="https://baike.baidu.com/item/%E5%BB%B6%E5%B9%B4%E7%9B%8A%E5%AF%BF/5737234">延年益寿</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">的佳品。如果选择适当的配料，还有其它功效。</span><span class="ref" style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">中医认为，芝麻性平味甘，有补肝肾、益精血、润肠燥的功效。</span>\n</p>\n<p>\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">食用指南需要提醒的是，芝麻所含的脂肪酸是以多元不饱和脂肪酸为主的。如果保存不当，比较容易氧化变质。因此，保存芝麻食品时，最好采用密封的方法，并存放在阴凉的地方，避免光照和高温。</span>\n</p>\n<p>\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">营养丰富，清甜麻香，可以解馋，余味无穷。黑芝麻有养颜保健和使白发变黑的功效。</span><a target="_blank" href="https://baike.baidu.com/item/%E9%BB%91%E8%8A%9D%E9%BA%BB">黑芝麻</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">中的</span><a target="_blank" href="https://baike.baidu.com/item/%E7%BB%B4%E7%94%9F%E7%B4%A0E">维生素E</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">非常丰富，可</span><a target="_blank" href="https://baike.baidu.com/item/%E5%BB%B6%E7%BC%93%E8%A1%B0%E8%80%81/4069179">延缓衰老</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">，并有润五脏，强筋骨、益气力等作用；</span><a target="_blank" href="https://baike.baidu.com/item/%E9%BB%91%E7%B1%B3/684820">黑米</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">、</span><a target="_blank" href="https://baike.baidu.com/item/%E7%BA%A2%E8%B1%86">红豆</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">可以瘦身；</span><a target="_blank" href="https://baike.baidu.com/item/%E8%96%8F%E7%B1%B3/3116601">薏米</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">可以活血养颜，富含膳食纤维的</span><a target="_blank" href="https://baike.baidu.com/item/%E8%96%8F%E7%B1%B3/3116601">薏米</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">还有预防</span><a target="_blank" href="https://baike.baidu.com/item/%E9%AB%98%E8%A1%80%E8%84%82">高血脂</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">、高血压、中风及心血管疾病的功效等等。</span>\n</p>\n<p>\n	<br />\n</p>', 'u2760874213959586776fm26gp0.jpg', 39, 4),
(35, '杏仁糊', '<p>\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">杏仁糊属于保健饮品，主要原料杏仁，调料为</span><a target="_blank" href="https://baike.baidu.com/item/%E7%99%BD%E7%B3%96/4821484">白糖</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">，制作方法为杏仁用热水浸泡，去内皮后捣泥，取出放锅中，锅内加糖、适量温水，放火上煮沸即成。口味甜香，工艺是煮，制作难度属于初级。</span>\n</p>\n<p>\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">杏仁的营养丰富，含有丰富的蛋白质，脂肪，而杏仁中的脂肪主要的成分是亚油酸和油酸，杏仁还带有一种独特的香味，那是其他坚果所没有的。</span>\n</p>\n<p>\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">苦杏仁中含有的脂肪油可以促使我们皮肤的角质层变软，还能让干燥的皮肤变得滋润，锁住皮肤里的水分，而且杏仁中被水解酶分解生成HCN，HCN能够一直人体内酪氨酸酶的活性，从而能够消除色素沉淀，黑斑及雀斑等等，所以食用杏仁可以养颜美容。</span>\n</p>', 'u38155751622112228610fm26gp0.jpg', 28, 4),
(36, '花生糊', '<p>\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">花生糊是是一道美味可口的传统名点，属于粤菜系</span><a target="_blank" href="https://baike.baidu.com/item/%E7%94%9C%E5%93%81/10777485">甜品</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">之一。把花生磨成末状后，用温水或沸水冲开，就可以食用了。为使口感更佳，在磨花生时也会加小量白米。花生糊通常是热食，尤其是在冬天时食用，可以温暖身体。经常食用可以补血养颜，强体健身。而且对治疗面色苍白，精神不振，手足不温，腰脚无力有益。而且制作简单适合家庭</span><a target="_blank" href="https://baike.baidu.com/item/DIY">DIY</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">或经营销售。</span>\n</p>\n<p>\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">花生糊是用花生做成的糊状物，是一道</span><a target="_blank" href="https://baike.baidu.com/item/%E9%A6%99%E6%B8%AF">香港</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">传统甜汤。做法是将花生磨成末状后，用温水或沸水冲开，就可以食用了。为使口感更佳，在磨花生时也会加小量白米。花生糊通常是热食，尤其是在冬天时食用，可以温暖身体。</span>\n</p>', 'u37161016621790248061fm26gp0.jpg', 44, 4),
(37, '百合糖水', '<p>\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">\n	<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n		山药百合糖水是一道美味可口的名点，属于粤菜系，具有清肺润燥、滋阴清热、理脾健胃的作用。\n	</div>\n	<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n		制作主要原料有<a target="_blank" href="https://baike.baidu.com/item/%E5%B1%B1%E8%8D%AF/414450">山药</a>50克、干百合100克、红枣10粒。制作方法一般为：先将干百合、红枣清洗干净，山药去皮，切成4厘米，宽1厘米的快锅内放入500毫升清水，大火煮开后将山药、百合、红枣一同放入锅、冰糖50克中，改小火煮45分钟，待水余下一半时，将冰糖放入至完全化开即可。\n	</div>\n	<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n		这道糖水的主角是百合，非常容易的就可以做出一碗好吃又好看的糖水!百合有清肺润燥、滋阴清热、理脾健胃的作用。山药具有健脾益胃助消化，滋肾益精，益肺止咳，降低血糖等功效。宁心安神，补脾胃，益肺肾。\n	</div>\n</span><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;"></span>\n</p>', 'u33315761202087160990fm26gp0.jpg', 37, 4);
INSERT INTO `hotel_commodity` (`id`, `subject`, `content`, `image`, `price`, `category_id`) VALUES
(38, '窝蛋奶', '<p>\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;"> </span>\n</p>\n<p style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">窝蛋奶是一种简单易做的</span><a target="_blank" href="https://baike.baidu.com/item/%E7%94%9C%E5%93%81/10777485">甜品</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">。它营养价值丰富，入口嫩滑，甜而不腻，很适合做给小朋友们吃。</span>\n</p>\n<p style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n	<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n		1.牛奶选用包装的无糖牛奶，煮牛奶时注意火候，看见牛奶起泡就应该把火力调小，否则牛奶容易溅出。\n	</div>\n	<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n		2、鸡蛋根据个人喜好掌握生、熟的程度，但三四分钟蛋已经完全熟透，所以不要超过这个时间。\n	</div>\n	<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n		3、最好选用细口的容器，这样不用花费太多的牛奶，又可以防止鸡蛋放入牛奶后会“黏底”（粘锅）\n	</div>\n</p>\n<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;"></span> \n<p>\n	<br />\n</p>', 'u32556175203892839727fm26gp0.jpg', 19, 4),
(39, '姜撞奶', '<p>\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;"> </span> \n</p>\n<p style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">姜撞奶是广东省广州市的一种特色小吃，属于粤菜，该小吃以姜汁和牛奶为主要原料，加工制作而成，姜撞奶味道香醇爽滑，甜中微辣、风味独特且有暖胃表热作用。</span>\n</p>\n<p style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">相传在广东番禺沙湾镇，一个年迈的老婆婆犯了咳嗽病，后知道姜汁可治咳嗽，但姜汁太辣，老婆婆无法喝下去，媳妇不小心把奶倒入装姜汁的碗里，奇怪的是过了一阵子牛奶凝结了，婆婆喝了后顿觉满口清香。第二天病就好了。因此姜撞奶就在沙湾镇流传开了，沙湾人把“凝结”叫“埋”于是“姜撞奶”在沙湾也叫“姜埋奶”。</span><span class="sup--normal" style="line-height:0;vertical-align:baseline;color:#3366CC;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">&nbsp;[1]</span>\n</p>', 'u1437577017869344182fm2gp0.jpg', 30, 4),
(40, '双皮奶', '<p>\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;"> </span> \n</p>\n<p style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">双皮奶，是一种</span><a target="_blank" href="https://baike.baidu.com/item/%E7%B2%A4/9830969">粤</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">式</span><a target="_blank" href="https://baike.baidu.com/item/%E7%94%9C%E5%93%81/10777485">甜品</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">。</span><a target="_blank" href="https://baike.baidu.com/item/%E6%B8%85%E6%9C%9D/175141">清朝</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">时起源自广东顺德，用</span><a target="_blank" href="https://baike.baidu.com/item/%E6%B0%B4%E7%89%9B%E5%A5%B6">水牛奶</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">做原料。现遍布于</span><a target="_blank" href="https://baike.baidu.com/item/%E5%B9%BF%E4%B8%9C/207811">广东</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">、</span><a target="_blank" href="https://baike.baidu.com/item/%E6%BE%B3%E9%97%A8/24335">澳门</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">、</span><a target="_blank" href="https://baike.baidu.com/item/%E9%A6%99%E6%B8%AF/128775">香港</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">等地。</span>\n</p>\n<p style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">据说，顺德双皮奶始创于</span><a target="_blank" href="https://baike.baidu.com/item/%E6%B8%85%E6%9C%9D/175141">清朝</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">末期，是顺德当地一位农民在清晨烹制</span><a target="_blank" href="https://baike.baidu.com/item/%E6%97%A9%E9%A4%90/617">早餐</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">的时候，不小心在水牛奶里翻了个花样，无意中调出民间美食“双皮奶”，并流传至今。</span><span class="sup--normal" style="line-height:0;vertical-align:baseline;color:#3366CC;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">&nbsp;</span>\n</p>\n<p style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">牛奶所含的</span><a target="_blank" href="https://baike.baidu.com/item/%E7%A2%B3%E6%B0%B4%E5%8C%96%E5%90%88%E7%89%A9">碳水化合物</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">中最丰富的是乳糖，乳糖使钙易于被吸收；含有丰富的</span><a target="_blank" href="https://baike.baidu.com/item/%E8%9B%8B%E7%99%BD%E8%B4%A8/309120">蛋白质</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">，包括</span><a target="_blank" href="https://baike.baidu.com/item/%E9%85%AA%E8%9B%8B%E7%99%BD/7143226">酪蛋白</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">和少量的</span><a target="_blank" href="https://baike.baidu.com/item/%E4%B9%B3%E6%B8%85%E8%9B%8B%E7%99%BD/7143898">乳清蛋白</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">；包括大量人体生长发育所需的</span><a target="_blank" href="https://baike.baidu.com/item/%E6%B0%A8%E5%9F%BA%E9%85%B8">氨基酸</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">；</span><a target="_blank" href="https://baike.baidu.com/item/%E9%92%99/720818">钙</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">含量很容易被吸收，另外，</span><a target="_blank" href="https://baike.baidu.com/item/%E7%A3%B7/722011">磷</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">、</span><a target="_blank" href="https://baike.baidu.com/item/%E9%92%BE/156200">钾</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">、</span><a target="_blank" href="https://baike.baidu.com/item/%E9%95%81">镁</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">等多种物质的搭配也十分合理；所含有的</span><a target="_blank" href="https://baike.baidu.com/item/%E8%84%82%E8%82%AA">脂肪</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">溶点低，</span><a target="_blank" href="https://baike.baidu.com/item/%E9%A2%97%E7%B2%92/17112136">颗粒</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">小，很容易被人体</span><a target="_blank" href="https://baike.baidu.com/item/%E6%B6%88%E5%8C%96/403663">消化</a><a target="_blank" href="https://baike.baidu.com/item/%E5%90%B8%E6%94%B6/5629743">吸收</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">，并其</span><a target="_blank" href="https://baike.baidu.com/item/%E6%B6%88%E5%8C%96%E7%8E%87/8593560">消化率</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">达97%。</span>\n</p>', 'u4084842063560920548fm26gp0.jpg', 43, 4),
(41, '银耳炖木瓜', '<p>\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;"> </span> \n</p>\n<p style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">木瓜炖银耳是一种美食，由木瓜、银耳、冰糖等制成。有利于</span><a target="_blank" href="https://baike.baidu.com/item/%E5%85%BB%E9%98%B4%E6%B6%A6%E8%82%BA/7181355">养阴润肺</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">、滋润养颜。老少皆宜。木瓜含有丰富的木瓜酶，能帮助滋润</span><a target="_blank" href="https://baike.baidu.com/item/%E8%82%8C%E8%82%A4/719327">肌肤</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">、尽快排除体内毒素；红枣可润心肺、补五脏、治虚损。此款糖水能美白润肤。</span>\n</p>\n<p style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n	<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n		1、银耳用水泡发一小时这样，冲洗干净后，去蒂摘成小碎片。\n	</div>\n	<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n		2、木瓜去外皮，剖开为二，去掉中间的瓤切成小块。（如果木瓜买回来熟的比较透的话，也可以把木瓜洗净，不用去皮。）\n	</div>\n	<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n		3、汤锅中倒入没过银耳的水，盖上盖子，大火烧开后，转小火先煮30分钟。\n	</div>\n	<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n		4、然后再倒入木瓜，加入适量的<a target="_blank" href="https://baike.baidu.com/item/%E5%86%B0%E7%B3%96/777029">冰糖</a>，大火烧开后，转小火也30分钟这样即可。\n	</div>\n</p>', 'u5963752293428507951fm26gp0.jpg', 29, 4),
(42, '芝麻汤圆', '<p>\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;"> </span> \n</p>\n<p style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">黑芝麻汤圆是一道小吃，制作原料主要有糯米粉、黑芝麻，黑芝麻汤圆属于浙菜系。此小吃香甜可口，美味无比。</span>\n</p>\n<p style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">黑芝麻汤圆是一道色香味俱全的传统名点，黑芝麻的神奇功效，还在于它含有的维生素E居植物性食品之首。维生素E能促进细胞分裂，推迟细胞衰老，常食可抵消或中和细胞内衰物质“游离基”的积累，起到抗衰老和延年益寿的作用。</span>\n</p>\n<p style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">像个小球白白嫩嫩肥肥，一口咬下去，软而不黏牙，甜而不腻，芝麻香滑可口</span>\n</p>\n<p>\n	<br />\n</p>', 'u14877087423710893324fm26gp0.jpg', 55, 4),
(43, '五粮液', '<p>\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">四川省宜宾五粮液集团有限公司，成立于1997年8月19日，位于四川省宜宾市，是一家以五粮液及其一系列酒的生产经营为主的国有独资公司。</span>\n</p>\n<p>\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">公司已经成为全球规模最大、生态环境最佳、五种粮食发酵、品质最优、古老与现代完美结合的酿酒圣地。公司下属5个子集团公司、12个子公司，占地10平方公里，现有职工30000人。2009年，五粮液集团公司继续保持了科学健康的良性发展，全年实现销售收入350.3亿元，同比增长16.5%；实现利税70亿元，同比增长16.8%，提前一年实现了“十一五”利税目标。五粮液品牌价值更是高达472.06亿元，位居全国最有价值品牌第四位，连续15年保持中国食品品牌价值之冠。</span>\n</p>', 'u3469808927392310230fm26gp0.jpg', 159, 5),
(44, '泸州老窖', '<p>\n	<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n		四川泸州老窖(jiào)是中国最古老的<a target="_blank" href="https://baike.baidu.com/item/%E5%9B%9B%E5%A4%A7%E5%90%8D%E9%85%92/2822230">四大名酒</a>之一，“浓香鼻祖，酒中泰斗”，中国大型白酒上市公司（深交所股票代码000568）。\n	</div>\n	<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n		其1573国宝窖池群1996年成为行业首家<a target="_blank" href="https://baike.baidu.com/item/%E5%85%A8%E5%9B%BD%E9%87%8D%E7%82%B9%E6%96%87%E7%89%A9%E4%BF%9D%E6%8A%A4%E5%8D%95%E4%BD%8D/2960685">全国重点文物保护单位</a>，传统酿制技艺2006年又入选首批<a target="_blank" href="https://baike.baidu.com/item/%E5%9B%BD%E5%AE%B6%E7%BA%A7%E9%9D%9E%E7%89%A9%E8%B4%A8%E6%96%87%E5%8C%96%E9%81%97%E4%BA%A7%E5%90%8D%E5%BD%95/2490096">国家级非物质文化遗产名录</a>，世称“双国宝单位”，旗下产品国窖1573被誉为“活文物酿造”、“中国白酒鉴赏标准级酒品”。\n	</div>\n	<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n		2015年12月25日，原泸州老窖集团副总裁<a target="_blank" href="https://baike.baidu.com/item/%E5%AD%99%E8%B7%83/19182693">孙跃</a>正式出任泸州老窖集团公司总裁、副董事长、党委副书记；原泸州老窖集团总裁<a target="_blank" href="https://baike.baidu.com/item/%E5%BC%A0%E8%89%AF/10481371">张良</a>出任集团董事长、党委书记。\n	</div>\n	<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n		2019年12月，泸州老窖入选2019中国品牌强国盛典榜样100品牌。<span class="sup--normal" style="font-size:12px;line-height:0;vertical-align:baseline;color:#3366CC;">&nbsp;[1]</span><a name="ref_[1]_45043"></a>&nbsp;\n	</div>\n	<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n		2020年9月，中国企业500强榜单揭晓，泸州老窖营收增长超过97%，此次排位直冲到366名。\n	</div>\n</p>', '2123522141517422208fm26gp0.jpg', 259, 5),
(45, '剑南春', '<p>\n	<br />\n</p>\n<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n	<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n		剑南春是中国传统<a target="_blank" href="https://baike.baidu.com/item/%E7%99%BD%E9%85%92/504313">白酒</a>品牌，产于四川省<a target="_blank" href="https://baike.baidu.com/item/%E7%BB%B5%E7%AB%B9%E5%B8%82/1468172">绵竹市</a>，与<a target="_blank" href="https://baike.baidu.com/item/%E7%BB%B5%E7%AB%B9%E6%9C%A8%E7%89%88%E5%B9%B4%E7%94%BB/5409655">绵竹木版年画</a>、<a target="_blank" href="https://baike.baidu.com/item/%E8%B5%B5%E5%9D%A1%E8%8C%B6/2083206">赵坡茶</a>并称为“<a target="_blank" href="https://baike.baidu.com/item/%E7%BB%B5%E7%AB%B9%E4%B8%89%E7%BB%9D/50318669">绵竹三绝</a>”<span class="sup--normal" style="font-size:12px;line-height:0;vertical-align:baseline;color:#3366CC;">&nbsp;[1-2]</span><a name="ref_[1-2]_54822"></a>&nbsp;&nbsp;。\n	</div>\n	<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n		因绵竹在<a target="_blank" href="https://baike.baidu.com/item/%E5%94%90%E4%BB%A3/345315">唐代</a>属剑南道，故称“剑南春”。其主要原料为<a target="_blank" href="https://baike.baidu.com/item/%E7%B3%AF%E7%B1%B3/118479">糯米</a>、<a target="_blank" href="https://baike.baidu.com/item/%E5%A4%A7%E7%B1%B3/22607">大米</a>、<a target="_blank" href="https://baike.baidu.com/item/%E7%8E%89%E7%B1%B3/18401">玉米</a>、<a target="_blank" href="https://baike.baidu.com/item/%E5%B0%8F%E9%BA%A6/10237">小麦</a>、<a target="_blank" href="https://baike.baidu.com/item/%E9%AB%98%E7%B2%B1/2862">高粱</a><span class="sup--normal" style="font-size:12px;line-height:0;vertical-align:baseline;color:#3366CC;">&nbsp;[3]</span><a name="ref_[3]_54822"></a>&nbsp;&nbsp;、水<span class="sup--normal" style="font-size:12px;line-height:0;vertical-align:baseline;color:#3366CC;">&nbsp;[4]</span><a name="ref_[4]_54822"></a>&nbsp;&nbsp;，主要产品有<a target="_blank" href="https://baike.baidu.com/item/%E5%89%91%E5%8D%97%E6%98%A5%E9%85%92/7052409">剑南春酒</a>、<a target="_blank" href="https://baike.baidu.com/item/%E7%8F%8D%E8%97%8F%E7%BA%A7%E5%89%91%E5%8D%97%E6%98%A5/1891739">珍藏级剑南春</a>、<a target="_blank" href="https://baike.baidu.com/item/%E9%87%91%E5%89%91%E5%8D%97/16845942">金剑南</a>、<a target="_blank" href="https://baike.baidu.com/item/%E4%B8%9C%E6%96%B9%E7%BA%A2/22592993">东方红</a>等。\n	</div>\n	<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n		2008年6月，剑南春酒传统酿造技艺入选国家级非物质文化遗产<span class="sup--normal" style="font-size:12px;line-height:0;vertical-align:baseline;color:#3366CC;">&nbsp;[5]</span><a name="ref_[5]_54822"></a>&nbsp;&nbsp;。2018年4月，剑南春位列国际终极烈酒挑战赛中国白酒产品第一名，荣获最高奖“主席奖杯”<span class="sup--normal" style="font-size:12px;line-height:0;vertical-align:baseline;color:#3366CC;">&nbsp;[6]</span><a name="ref_[6]_54822"></a>&nbsp;&nbsp;。\n	</div>\n<br />\n</div>\n<p>\n	<br />\n</p>', 'u3693009581663516311fm26gp0.jpg', 359, 5),
(46, '可口可乐', '<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n	1886年，可口可乐在美国<a target="_blank" href="https://baike.baidu.com/item/%E4%B9%94%E6%B2%BB%E4%BA%9A%E5%B7%9E/2414335">乔治亚州</a><a target="_blank" href="https://baike.baidu.com/item/%E4%BA%9A%E7%89%B9%E5%85%B0%E5%A4%A7/11926">亚特兰大</a>市诞生，自此便与社会发展相互交融，激发创新灵感。现在，它每天为全球的人们带来怡神畅快的美妙感受。\n</div>\n<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n	目前，全球每天有17亿人次的消费者在畅饮可口可乐公司的产品，大约每秒钟售出19,400瓶<a target="_blank" href="https://baike.baidu.com/item/%E9%A5%AE%E6%96%99">饮料</a>，在2016年10月，可口可乐公司排2016年全球100大最有价值品牌第三名<span class="sup--normal" style="font-size:12px;line-height:0;vertical-align:baseline;color:#3366CC;">&nbsp;[1]</span><a name="ref_[1]_5842"></a>&nbsp;&nbsp;；可口可乐为中国消费者提供超过15个品牌50多种饮料选择，其系列产品在华的每天享用量达到1.5亿杯，<span class="sup--normal" style="font-size:12px;line-height:0;vertical-align:baseline;color:#3366CC;">&nbsp;[2]</span><a name="ref_[2]_5842"></a>&nbsp;&nbsp;可口可乐自1979年重返中国市场至2014年底，已累计投资超过90亿美元，目前在华建有43家工厂，系统员工约45,000人，其中99%为本地员工，<span class="sup--normal" style="font-size:12px;line-height:0;vertical-align:baseline;color:#3366CC;">&nbsp;[3]</span><a name="ref_[3]_5842"></a>&nbsp;&nbsp;可口可乐及其装瓶厂在中国长期以来不遗余力地支持教育及公益事业，推广环境保护以及帮助当地社区的发展，捐资总额超过2.7亿元人民币，另外，可口可乐亦是唯一一个全方位赞助在中国举办的<a target="_blank" href="https://baike.baidu.com/item/%E7%89%B9%E5%A5%A5%E4%BC%9A/251377">特奥会</a>、<a target="_blank" href="https://baike.baidu.com/item/%E5%A5%A5%E8%BF%90%E4%BC%9A/106463">奥运会</a>、<a target="_blank" href="https://baike.baidu.com/item/%E6%AE%8B%E5%A5%A5%E4%BC%9A/11168942">残奥会</a>、<a target="_blank" href="https://baike.baidu.com/item/%E4%B8%96%E5%8D%9A%E4%BC%9A/171154">世博会</a>、<a target="_blank" href="https://baike.baidu.com/item/%E5%A4%A7%E8%BF%90%E4%BC%9A/9936118">大运会</a>及<a target="_blank" href="https://baike.baidu.com/item/%E9%9D%92%E5%A5%A5%E4%BC%9A/11222276">青奥会</a>的企业。\n</div>', 'u12870902103053473278fm26gp0.jpg', 4, 5),
(47, '果粒橙', '<p style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n	<a target="_blank" href="https://baike.baidu.com/item/%E7%BE%8E%E6%B1%81%E6%BA%90/149111">美汁源</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">是</span><a target="_blank" href="https://baike.baidu.com/item/%E5%8F%AF%E5%8F%A3%E5%8F%AF%E4%B9%90%E5%85%AC%E5%8F%B8/1612740">可口可乐公司</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">2004年4月份上市的一个品牌，是全球销量最大的</span><a target="_blank" href="https://baike.baidu.com/item/%E6%9E%9C%E6%B1%81/1176606">果汁</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">品牌之一。美汁源创立于</span><a target="_blank" href="https://baike.baidu.com/item/%E7%BE%8E%E5%9B%BD/125486">美国</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">，拥有超过60年的专业果汁生产经验。2004年美汁源到中国并成功推出了“美汁源果粒橙“含真正果肉的</span><a target="_blank" href="https://baike.baidu.com/item/%E6%A9%99%E6%B1%81/2836411">橙汁</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">饮料。目前，该产品</span><a target="_blank" href="https://baike.baidu.com/item/%E5%88%86%E9%94%80/1800508">分销</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">100 多个国家不添加</span><a target="_blank" href="https://baike.baidu.com/item/%E9%98%B2%E8%85%90%E5%89%82/2532023">防腐剂</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">和</span><a target="_blank" href="https://baike.baidu.com/item/%E4%BA%BA%E9%80%A0%E8%89%B2%E7%B4%A0/708680">人造色素</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">。</span>\n</p>\n<p style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">美汁源（Minute Maid）是可口可乐在全球使用多年的果汁品牌。最早可追溯到1946年4月15日第一批从美国佛罗里达运出的冷冻浓缩橙汁，此举标志着现代果汁</span><a target="_blank" href="https://baike.baidu.com/item/%E5%88%B6%E9%80%A0%E4%B8%9A">制造业</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">的诞生。</span><a target="_blank" href="https://baike.baidu.com/item/%E5%8F%AF%E5%8F%A3%E5%8F%AF%E4%B9%90">可口可乐</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">希望借新果汁引入这一全球品牌。但问题在于，Minute Maid针对全球不同市场有不同的产品定位，并对应着各具文化特质的本地翻译。如何把一个50多年前诞生的英文翻译成汉语、并能恰如其分地表达品牌含义？林邦源再次被难住了。</span>\n</p>', 'u3173558848911976369fm26gp0.jpg', 5, 5),
(48, '柠檬C', '<p style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">柠檬C是一种重要的有机酸，又名</span><a target="_blank" href="https://baike.baidu.com/item/%E6%9E%B8%E6%A9%BC%E9%85%B8/1188933">枸橼酸</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">，无色晶体，常含一分子结晶水，无臭，有很强的酸味，易溶于水。其钙盐在冷水中比热水中易溶解，此性质常用来鉴定和分离柠檬酸。结晶时控制适宜的温度可获得无水柠檬酸。在工业，食品业，化妆业等具有极多的用途。</span>\n</p>\n<p style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">在室温下，柠檬C为无色半透明</span><a target="_blank" href="https://baike.baidu.com/item/%E6%99%B6%E4%BD%93">晶体</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">或白色颗粒或白色结晶性粉末，无臭、味极酸，在潮湿的空气中微有</span><a target="_blank" href="https://baike.baidu.com/item/%E6%BD%AE%E8%A7%A3%E6%80%A7">潮解性</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">。它可以以无水合物或者一水合物的形式存在：柠檬C从热水中结晶时，生成无水合物；在冷水中结晶则生成一水合物。加热到78 °C时一水合物会分解得到无水合物。在15摄氏度时，柠檬C也可在</span><a target="_blank" href="https://baike.baidu.com/item/%E6%97%A0%E6%B0%B4%E4%B9%99%E9%86%87">无水乙醇</a><span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">中溶解。</span>\n</p>', 'u24749648773113075125fm26gp0.jpg', 8, 5),
(49, 'o泡果奶', '<p style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n	<span style="color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;">2018年6月11日，国家广播电视总局下发通知，停止播出“O泡果奶”等违规广告。其中宣传“O泡果奶”“莎娃鸡尾酒”的电视广告，部分内容含有表现学生早恋和少男少女饮用酒精饮品感受等情节，价值导向存在偏差，易对未成年人产生误导，影响未成年人健康成长。</span>\n</p>', 'u5720124062364888884fm26gp0.jpg', 3, 5),
(50, '青岛啤酒', '<p style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n	<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n		青岛啤酒产自<a target="_blank" href="https://baike.baidu.com/item/%E9%9D%92%E5%B2%9B%E5%95%A4%E9%85%92%E8%82%A1%E4%BB%BD%E6%9C%89%E9%99%90%E5%85%AC%E5%8F%B8/6684207">青岛啤酒股份有限公司</a>，公司的前身是国营青岛啤酒厂，1903年由<a target="_blank" href="https://baike.baidu.com/item/%E8%8B%B1/4636599">英</a>、<a target="_blank" href="https://baike.baidu.com/item/%E5%BE%B7">德</a>两国商人合资开办，是最早的啤酒生产企业之一。2008年<a target="_blank" href="https://baike.baidu.com/item/%E5%8C%97%E4%BA%AC%E5%A5%A5%E8%BF%90%E4%BC%9A">北京奥运会</a>官方赞助商，跻身<a target="_blank" href="https://baike.baidu.com/item/%E4%B8%96%E7%95%8C%E5%93%81%E7%89%8C500%E5%BC%BA/10173844">世界品牌500强</a>。\n	</div>\n	<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n		青岛啤酒选用优质<a target="_blank" href="https://baike.baidu.com/item/%E5%A4%A7%E9%BA%A6/687074">大麦</a>、<a target="_blank" href="https://baike.baidu.com/item/%E5%A4%A7%E7%B1%B3/22607">大米</a>、上等<a target="_blank" href="https://baike.baidu.com/item/%E5%95%A4%E9%85%92%E8%8A%B1/4454">啤酒花</a>和软硬适度、洁净甘美的<a target="_blank" href="https://baike.baidu.com/item/%E5%B4%82%E5%B1%B1%E7%9F%BF%E6%B3%89%E6%B0%B4/6642294">崂山矿泉水</a>为原料酿制而成。原麦汁浓度为十二度，酒精含量3．5－4%。酒液清澈透明、呈淡黄色，泡沫清白、细腻而持久。2018年12月18日，世界品牌实验室发布《<a target="_blank" href="https://baike.baidu.com/item/2018%E4%B8%96%E7%95%8C%E5%93%81%E7%89%8C500%E5%BC%BA/23215459">2018世界品牌500强</a>》，青岛啤酒排名第310。<span class="sup--normal" style="font-size:12px;line-height:0;vertical-align:baseline;color:#3366CC;">&nbsp;[1]</span><a name="ref_[1]_18992"></a>&nbsp;\n	</div>\n	<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n		2019年4月10日，青岛啤酒第41届“提高质量纪念日”主题活动——“匠心筑梦，开创魅力质量新时代”在青岛啤酒二厂举行<span class="sup--normal" style="font-size:12px;line-height:0;vertical-align:baseline;color:#3366CC;">&nbsp;[2]</span><a name="ref_[2]_18992"></a>&nbsp;&nbsp;。\n	</div>\n	<div class="para" style="font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\n		2019年12月，青岛啤酒入选2019中国品牌强国盛典榜样100品牌。<span class="sup--normal" style="font-size:12px;line-height:0;vertical-align:baseline;color:#3366CC;">&nbsp;[3]</span><a name="ref_[3]_18992"></a>&nbsp;&nbsp;2019年12月18日，人民日报“中国品牌发展指数”100榜单排名第91位。\n	</div>\n</p>', 'u22721975983230367596fm26gp0.jpg', 9, 5);

