--
-- 表的结构 `hotel_resgiter`
--

CREATE TABLE `hotel_resgiter` (
  `id` mediumint(8) UNSIGNED NOT NULL COMMENT '用户ID,主键且自增',
  `username` varchar(30) NOT NULL COMMENT '用户名,唯一约束',
  `password` varchar(32) NOT NULL COMMENT '登录密码,MD5',
  `phone` varchar(20) NOT NULL COMMENT '电话',
  `email` varchar(32) NOT NULL COMMENT '邮箱',
  `avatar` varchar(50) DEFAULT NULL COMMENT '用户头像'
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

