--
-- 表的结构 `hotel_storefront`
--

CREATE TABLE `hotel_storefront` (
  `id` int(10) UNSIGNED NOT NULL COMMENT '店面id，主键且自增',
  `subject` varchar(50) NOT NULL COMMENT '店面名称',
  `image` varchar(50) NOT NULL COMMENT '店面图片',
  `characteristic` varchar(100) NOT NULL COMMENT '特色菜品',
  `business` varchar(50) NOT NULL COMMENT '营业时间',
  `parking` varchar(50) NOT NULL COMMENT '停车泊位',
  `address` varchar(50) NOT NULL COMMENT '餐厅地址',
  `label` varchar(255) NOT NULL COMMENT '餐厅标签',
  `category_id` int(10) UNSIGNED NOT NULL COMMENT '外键,店面分类'
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

--
-- 转存表中的数据 `hotel_storefront`
